    <script>
        $('.cmn-btn').on('click', function() {
            var roomId = $(this).data('room-id');
            var roomPrice = $(this).data('price');
            var roomType = $(this).data('room-type');
            var roomDescription = $(this).data('room-description');
            var maxOccupancy = $(this).data('max-occupancy');
            var roomNumber = $(this).data('room-number');
            var bedCount = $(this).data('bed-count');
            var bedType = $(this).data('bed-type');
            var roomavailablefrom = $(this).data('room_available_from');
            var roomavailableuntil = $(this).data('room_available_until');

            $('#room_id').val(roomId);
            $('#price').val(roomPrice);
            $('#room_type').text(roomType);
            $('#room_description').text(roomDescription);
            $('#max_occupancy').text(maxOccupancy);
            $('#number_of_guests').attr('data-maxoccupancy', maxOccupancy);
            $('#room_number').text(roomNumber);
            $('#check_in').attr('data-availableuntil', roomavailableuntil);
            $('#check_out').attr('data-availablefrom', roomavailablefrom);
            $('#bed_count').text(bedCount);
            $('#bed_type').text(bedType);
            $('#total_price').text(roomPrice);
            $('#bookingModal').modal('show');
        });

        $(document).ready(function() {
            $('#check_in, #check_out').on('change', function() {
                var checkInDate = $('#check_in').val();
                var checkOutDate = $('#check_out').val();
                var availableUntilorg = new Date($('#check_in').data('availableuntil'));

                var availableFrom = new Date($('#check_out').data('availablefrom'));
                var availableUntil = new Date($('#check_in').data('availableuntil'));
                availableFrom.setDate(availableFrom.getDate() - 1);
                availableUntil.setDate(availableUntil.getDate() + 1);
                var checkIn = new Date(checkInDate);
                var checkOut = new Date(checkOutDate);
                var availableFromTime = availableFrom.getTime();
                var availableUntilTime = availableUntil.getTime();
                var checkInTime = checkIn.getTime();
                var checkOutTime = checkOut.getTime();
                if (
                    (checkInTime >= availableUntilTime && checkInTime < availableFromTime) ||
                    (checkOutTime > availableUntilTime && checkOutTime <= availableFromTime) ||
                    (checkInTime <= availableUntilTime && checkOutTime >=
                    availableFromTime)
                ) {
                    alert(
                        `The room is not available from ${availableUntilorg.toDateString()} to ${availableFrom.toDateString()}.`);
                    $('#total_price').text('0');
                    $('#check_in').val('');
                    $('#check_out').val('');
                    $('#number_of_guests').val('');
                    return;
                }
                if (checkOutTime <= checkInTime) {
                    alert('Check-out date must be later than check-in date.');
                    $('#total_price').text('0');
                    $('#check_in').val('');
                    $('#check_out').val('');
                    $('#number_of_guests').val('');
                    return;
                }
                var timeDifference = checkOutTime - checkInTime;
                var numberOfNights = timeDifference / (1000 * 3600 * 24);
                var pricePerNight = parseFloat($('#price').val());
                var totalPrice = pricePerNight * numberOfNights;

                $('#total_price').text(totalPrice.toFixed(2));
            });




            $('#bookingModal').on('hide.bs.modal', function() {
                $(this).fadeOut(300);
            });
            $('#bookingModal').on('show.bs.modal', function() {
                $(this).fadeIn(300);
            });
            $('.close').on('click', function() {
                $('#bookingModal').fadeOut(300, function() {
                    $('#bookingModal').modal('hide');
                });
            });

            $('#number_of_guests').on('input', function() {
                var numberOfGuests = $(this).val();
                var maxOccupancy = $(this).data('maxoccupancy');
                if (numberOfGuests === "" || numberOfGuests === "0") {
                    $('#confirmBookingBtn').prop('disabled', true);
                } else if (numberOfGuests > maxOccupancy) {
                    $('#confirmBookingBtn').prop('disabled', true);
                    alert('The number of guests exceeds the maximum occupancy.');
                } else {
                    $('#confirmBookingBtn').prop('disabled', false);
                }
            });
        });
    </script>

